query amazonSearch($searchOptions: SearchOptionsInputType){
  amazon {
    searchItemsByKeyword(searchOptions: $searchOptions) {
      responseStatus
      foundProducts
      searchProductDetails {
        asin
        productDescription
        imgUrl
      }
    }
  }
}

query GettingManyItems($productURLs: [String]!){
  amazon {
    getItemsByURL(productUrls: $productURLs) {
      asin
      categories
      currency {
        code
        symbol
      }
      features
      imageUrlList
      mainImage {
        imageUrl
      }
      price
      productDescription
      productDetails {
        name
        value
      }
      productTitle
      variations {
        variationName
        values {
          value
          dpUrl
          selected
          available
        }
      }
      warehouseAvailability
    }
  }
}

